    top: 50%;
    left: 50%;
    transform: translateX(-50%) translateY(-50%);
    z-index: 1;
    border-top: 1px solid #fff;
    border-bottom: 1px solid #fff;
}

.tl-year p {
    font-family: tahoma;
    font-size: 1.628rem;
}

.tl-bg {
    transform: translate3d(0,0,0);
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-size: cover;
    background-position: center center;
    transition: filter .5s ease;
}


.work {
    width: 100%;
    &__row {
        position: relative;
        font-family: $font-regular;
        width: 100%;
        height: 400px;
        margin-top: 100px;
        border-bottom: 1px solid $black;
    }
    &__image {
        height: 300px;
        display: block;
        width: 40%;
        float: right;
        background-image: url(../images/peek-work/1.png);
        background-size: cover;
        background-repeat: no-repeat;
    }
    &__text {
        float: left;
        margin-top: auto;
        margin-bottom: auto;
        text-align: left;
        width: 40%;
        color: $white;
        h2 {
            padding: 30px 0;
        }
        p {
            padding-bottom: 20px;
        }
        .global__button {
            padding-top: 50px;
        }
    }
    &__skills {
        position: absolute;
        top: 0;
        right: 0;
        cursor: pointer;
        height: 2004px;
        color: rgba(255, 255, 255, .5);
        text-align: center;
        width: 20%;
        font-size: 5rem;
        border-bottom: 1px solid $black;
        ul:first-child {
            margin-top: 600px;
        }
    }
    &__letter {
            margin: 50px 0;
    }
}



.services {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    // width: 80%;
    // border-right: 1px solid $black;
    // box-shadow: 0 0 5px 5px;
    // .work__wrap {
    //     // border-right: 1px solid $black;
    //     // box-shadow: 5px 5px 5px;
        
    // }
    .work__row {
        &:nth-child(1) {
            margin-top: 0;
            padding-top: 100px;
        }
    }
    h1 {
        color: $white;
        font-size: 1.5rem;
    }
    .services__inner {
        width: 80%;
        border-left: 1px solid $black;
        box-shadow: 0 0 5px 5px;
    }
    .services__link {
        cursor: pointer;
        color: rgba(255, 255, 255, .5);
        text-align: center;
        width: 20%;
        font-size: 5rem;
        &__letter {
            margin: 50px 0;
        }
    }
}





.skills {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
}

.skills__inner {
    width: 80%;
    border-left: 1px solid $black;
    box-shadow: 0 0 5px 5px;
}

.skills__link {
    cursor: pointer;
    color: rgba(255, 255, 255, .5);
    text-align: center;
    width: 20%;
    font-size: 5rem;
    &__letter {
        margin: 50px 0;
    }
}

.clear {
    clear: both;
}

